import { Input, Button } from "@mantine/core";
import { Dropzone, MIME_TYPES } from "@mantine/dropzone";
import style from "./style.module.css";
import React, { useState, useRef } from "react";
import { useCreateReviewMutation } from "../../services/review";
import { storage } from "../../firebase/index";
import { ref, uploadBytes } from "firebase/storage";
import {v4} from 'uuid'

function CreateOperation() {
  const [createReview] = useCreateReviewMutation();
  const [value, setValue] = useState({
    title: "",
    name: "",
    category: "",
    description: "",
    grade: "",
    genre: "",
    imageLink: "",
  });

  function changeInputValue(event: { target: { name: any; value: any } }) {
    setValue({ ...value, [event.target.name]: event.target.value });
  }

  function sendRequest() {
    createReview(value);
  }
  async function handleClick() {
    const imgFile = img; // Сохраните выбранный файл
    const imgRef = ref(storage, `files/${v4()}`); // Создайте уникальное имя файла
    await uploadBytes(imgRef, imgFile);
    const downloadURL = await getDownloadURL(imgRef); // Получите URL загруженного изображения
    setValue({ ...value, imageLink: downloadURL }); // Обновите состояние value с URL изображения
  }

  // const openRef = useRef<() => void>(null);

  const [img, setImg] = useState("");

  return (
    <div className={style.wrapper}>
      <h2>Review title</h2>
      <Input
        name="title"
        onChange={changeInputValue}
        placeholder="review title"
      />

      <h2>Name of film/book/game</h2>
      <Input
        name="name"
        onChange={changeInputValue}
        placeholder="review name"
      />

      <h2>Category</h2>
      <Input
        name="category"
        onChange={changeInputValue}
        placeholder="review category"
      />

      <h2>Description</h2>
      <Input
        name="description"
        onChange={changeInputValue}
        placeholder="review description"
      />

      <h2>Grade</h2>
      <Input
        name="grade"
        onChange={changeInputValue}
        placeholder="review grade"
      />

      <h2>Genre</h2>
      <Input
        name="genre"
        onChange={changeInputValue}
        placeholder="review genre"
      />

      <h2>Image</h2>
      {/* <Dropzone
        openRef={openRef}
        onDrop={(files) => uploadFile(files[0], files[0].name)}
      >
        Перетащите файл сюда или нажмите для выбора
      </Dropzone> */}
      <input type="file" onChange={(e) => setImg(e.target.files[0])} />

      <Button onClick={handleClick}>img</Button>
      <Button onClick={sendRequest}>GO</Button>
    </div>
  );
}
