import { Input, Button } from "@mantine/core";
import { Dropzone, MIME_TYPES } from "@mantine/dropzone";
import style from "./style.module.css";
import React, { useState } from "react";
import { useCreateReviewMutation } from "../../services/review";
import { storage, storageRef } from "../../firebase/index";
import { getDownloadURL, ref, uploadBytes } from "firebase/storage";

function CreateOperation() {
  const [createReview] = useCreateReviewMutation();
  const [value, setValue] = useState({
    title: "",
    name: "",
    category: "",
    description: "",
    grade: "",
    genre: "",
    imageLink: "",
  });

  async function handleImageUpload(files: File[]) {
    const file = files[0];
    const storageRef = ref(storage); // Получаем корневую ссылку на Firebase Storage

    // Создаем ссылку на место, куда мы хотим загрузить файл
    const imageRef = ref(storageRef, file.name); // 'images' - это папка, куда будет загружен файл

    try {
      const snapshot = await uploadBytes(imageRef, file);
      const downloadURL = await getDownloadURL(snapshot.ref);
      setValue({ ...value, imageLink: downloadURL });
    } catch (error) {
      console.error("Error uploading image:", error);
    }
  }

  function sendRequest() {
    createReview(value);
  }

  function changeInputValue(event: { target: { name: any; value: any } }) {
    setValue({ ...value, [event.target.name]: event.target.value });
  }

  return (
    <div className={style.wrapper}>
      <h2>Review title</h2>
      <Input
        name="title"
        onChange={changeInputValue}
        placeholder="review title"
      />

      <h2>Name of film/book/game</h2>
      <Input
        name="name"
        onChange={changeInputValue}
        placeholder="review name"
      />

      <h2>Category</h2>
      <Input
        name="category"
        onChange={changeInputValue}
        placeholder="review category"
      />

      <h2>Description</h2>
      <Input
        name="description"
        onChange={changeInputValue}
        placeholder="review description"
      />

      <h2>Grade</h2>
      <Input
        name="grade"
        onChange={changeInputValue}
        placeholder="review grade"
      />

      <h2>Genre</h2>
      <Input
        name="genre"
        onChange={changeInputValue}
        placeholder="review genre"
      />

      {/* Добавьте компонент Dropzone для загрузки изображения */}
      <Dropzone onDrop={handleImageUpload} accept={[MIME_TYPES.image]}>
  {({ over }):any => {
    return (
      <div
        style={{
          border: `2px dashed ${over ? "#16a085" : "#ccc"}`,
          borderRadius: "4px",
          padding: "20px",
          textAlign: "center",
        }}
      >
        Drag & drop an image here or click to select
      </div>
    );
  }}
</Dropzone>

      <Button onClick={sendRequest}>GO</Button>
    </div>
  );
}

export default CreateOperation;
