import React, { ReactNode } from 'react'
import cn from 'classnames'
import useTheme from '../../hooks/useTheme'

const Layout = ({ children }: { children: ReactNode }) => {
	const { isDark } = useTheme()

	return (
		<div
			className={cn('layout', {
				dark: isDark,
			})}
		>
			{children}
		</div>
	)
}

export default Layout
